import { createContext, ReactNode, useContext, useEffect, useState } from "react";
import { api } from "../services/api";


interface Transaction {
    id: number;
    title: string;
    amount: number;
    type: string;
    category:string;
    createdAt: string;
}

// interface TransactionInput {
//     title: string;
//     amount: number;
//     type: string;
//     category:string;
// }
type TransactionInput = Omit<Transaction, 'id' | 'createdAt'>

// type TransactionInput = Pick<Transaction, 'title' |'amount' | 'type' | 'category' >

interface TransactionProviderPropos {
    children: ReactNode;
}

interface TransactonsContextData {
    transactions: Transaction[];
    createTransaction: (transaction: TransactionInput) => Promise<void>;
}

const TransactionsContext = createContext<TransactonsContextData>(
    {} as TransactonsContextData

);

export function TransactionsProvider({children}: TransactionProviderPropos){
const [transactions , setTransactions] = useState<Transaction[]>([]);



useEffect(() => {
    api.get('transactions')
    .then(response=> setTransactions(response.data.transactions))
}, [])

async function createTransaction(transactionInput: TransactionInput) {
      const response = await api.post('/transactions', {
          ...transactionInput,
          createdAt: new Date(),
    });
      const {transaction } = response.data;

      setTransactions([...transactions, transaction])
}

return (
    <TransactionsContext.Provider value={{ transactions , createTransaction} }>
        {children}
    </TransactionsContext.Provider>
);
}

export function useTransactions (){
    const context = useContext(TransactionsContext);

    return context;
}
